import React,{useState,useEffect} from 'react';
import Layout from './Layout';
import {read} from './apiCore';
import Card from './Card';
import ShowImage from './ShowImage';
import moment from 'moment';

const Product=(props)=>{

    const [product,setProduct]=useState({});
    const [error,setError]=useState(false);

    const loadSingleProduct=productId=>{
        read(productId)
        .then(data=>{
            if(data.error){
                setError(data.error);
            }else{
                setProduct(data);
            }
        });
    };

    useEffect(()=>{
        const productId=props.match.params.productId;   //props object is provided by react-router-dom
            //we are extracting productId FROM URL at the time the Product component is mounted. This 
            //is because we want to display all the info as soon as the page loads.
        loadSingleProduct(productId);
    },[]);


    const showAddToCardButton=()=>{
        return(
            <button className="btn btn-outline-warning mt-2 mb-2">
                Add to Cart
            </button>
        )
    }

    return (
    <div style={{backgroundImage: "linear-gradient(rgba(0,0,0,0.1),rgba(0,0,0,0.1))"}}>
        <Layout title={product && product.name} 
            description={product.category && product.category.name} className="container-fluid" >
            
            <div className="row" >
                <div className="col-4 mt-10">
                    <ShowImage item={product} url="product" />
                </div>
                <div className="col-6 " 
                    style={{margin:"30px",whiteSpace:"pre-wrap",
                               marginRight:"40" }}>
                        <h2>Description</h2>
                        <p className="lead">
                            {(product.description) }
                        </p>
                        <p>
                            <h4>Price :</h4> 
                            <h6>{"Rs "+product.price}</h6>
                        </p>
                        <p>
                            <h6>Added {moment(product.createdAt).fromNow()}...</h6>
                        </p>
                        <p>
                            {showAddToCardButton()}
                        </p>                    
                </div>
            </div>
            <div className="mt-4">
                <h2>Related Products</h2>
            </div>            
        </Layout>  
    </div> 
    );
};

export default Product;
