import React,{useState,useEffect} from 'react';
import Layout from './Layout';
import {read} from './apiCore';
import Card from './Card';
import ShowImage from './ShowImage';

const Product=(props)=>{

    const [product,setProduct]=useState({});
    const [error,setError]=useState(false);

    const loadSingleProduct=productId=>{
        read(productId)
        .then(data=>{
            if(data.error){
                setError(data.error);
            }else{
                setProduct(data);
            }
        });
    };

    useEffect(()=>{
        const productId=props.match.params.productId;   //props object is provided by react-router-dom
            //we are extracting productId FROM URL at the time the Product component is mounted. This 
            //is because we want to display all the info as soon as the page loads.
        loadSingleProduct(productId);
    },[]);

    return (
        <Layout title={product && product.name} 
            description={`  `} className="container-fluid" >
            
            <div className="row">
                <div className="col-4 mt-10">
                    <ShowImage item={product} url="product" />
                </div>
                <div className="col-6 " 
                    style={{border:"thick solid #0000FF",margin:"30px",whiteSpace:"normal",
                                wordBreak: "break-word",display: "contents"}}>
                        <h2>Description</h2>
                        <p style={{}}>
                            {(product.description)}
                        </p>
                    
                </div>
            </div>
            
        </Layout>    
    );
};

export default Product;
