import React,{useState,useEffect,Fragment} from 'react';
import Layout from './Layout';
import {getProducts} from './apiCore';
import Card from './Card';
import '../styles2.css'


const Checkout=({products})=>{

    const getTotal=()=>{
        return products.reduce((currentTotal,currentItem)=>{
            return currentTotal + (currentItem.count * currentItem.price);
        },0);
    };

    return (
        <Fragment>
            <table id="bill" className="mb-9">
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Price(Rs)</th>
                        <th>Quantity</th>
                        <th>Total(Rs)</th>
                    </tr>
                </thead>
                <tbody>
                {products.map((p,i)=>(
                    <tr key={i}>
                        <td>{p.name}</td>
                        <td>{p.price}</td>
                        <td>{p.count}</td>
                        <td>{p.count * p.price}</td>
                    </tr>
                ))}
                </tbody>
                <tfoot>
                    <tr>
                        <td colSpan="3"><center>Grand total:-</center></td>
                        <td>Rs {getTotal()} </td>
                    </tr>
                </tfoot>
            </table>   
        </Fragment>

    )
};

export default Checkout;
/*
<div style={{display:"inline"}}>
                <h5 style={{display:"inline"}}>Grand Total : </h5> Rs {getTotal()} 
            </div>
*/
