import React,{useState,useEffect} from 'react';
import Layout from './Layout';
import {read} from './apiCore';
import Card from './Card';

const Product=(props)=>{

    const [product,setProduct]=setState({});
    const [error,setError]=setState(false);

    const loadSingleProduct=productId=>{
        read(productId)
        .then(data=>{
            if(data.error){
                setError(data.error);
            }else{
                setProduct(data);
            }
        });
    };

    useEffect(()=>{
        const productId=props.match.params.productId;   //props object is provided by react-router-dom
            //we are extracting productId FROM URL at the time the Product component is mounted. This 
            //is because we want to display all the info as soon as the page loads.
        loadSingleProduct(productId);
    },[]);

    return (
        <Layout title={product && product.name} 
            description={product && product.description} className="container-fluid" >
            
            <div className="row">
                
            </div>
        </Layout>    
    );
};

export default Product;
