import React from 'react';
import {Link} from 'react-router-dom';
import ShowImage from './ShowImage';

const showStock=quantity=>{
    return quantity>0 ? (
        quantity<5 ? (
            <span className="badge badge-primary badge-pill">Only {quantity} pieces left.Hurry..! Order Now.</span>
        ):(
            <span className="badge badge-primary badge-pill">In Stock</span>
        )
    ):(
        <span className="badge badge-primary badge-pill">Out of Stock</span>
    )
};

const showAddToCartButton=quantity=>{
    return quantity>0 ?(
        <button className="btn btn-outline-warning mr-1 ml-2 ">
            Add to Cart
        </button>
    ):(
        <button className="btn btn-outline-warning mr-1 ml-2" disabled>
            Add to Cart
        </button>
    )
};


const Card=({product})=>{
    return(
        <div className="col-4 mb-3" >
            <div className="card" style={{position:"relative"}} >
                <div className="card-header">{product.name}</div>
                <div className="card-body">
                    <div className="mb-1" style={{width:"200px",height:"250px",position:"relative"}}>
                        <ShowImage item={product} url="product" /> 
                    </div>
                    <p>{product.description.substring(0,25)}</p>
                    <p>Rs {product.price}</p>
                    <p>{showStock(product.quantity)}</p>
                    <Link to={`/product/${product._id}`}>
                        <button className="btn btn-outline-primary mt-2 mb-2">
                            View Product
                        </button>
                    </Link>
                    {showAddToCartButton(product.quantity)}
                    <div>
                        
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Card;
