import React,{useState,useEffect,Fragment} from 'react';
import {Link} from 'react-router-dom';
import Layout from './Layout';
import {getProducts,getBraintreeClientToken,processPayment} from './apiCore';
import Card from './Card';
import {isAuthenticated} from '../auth';
import '../styles2.css'
import DropIn from 'braintree-web-drop-in-react';


const Checkout=({products})=>{

    const [data,setData]=useState({
        success:false,
        clientToken: null,
        error:'',
        instance:{},
        address:''
    });

    const userId=isAuthenticated() && isAuthenticated().user._id;
    const token=isAuthenticated() && isAuthenticated().token;

    const getToken=(userId,token)=>{
        getBraintreeClientToken(userId,token)
            .then(data=>{
                if(data.error){
                    setData({...data,error: data.error});
                }else{
                    setData({clientToken:data.clientToken});
                }
            })
    }

    useEffect(()=>{
        getToken(userId,token);
    },[]);

    const getTotal=()=>{
        return products.reduce((currentTotal,currentItem)=>{
            return currentTotal + (currentItem.count * currentItem.price);
        },0);
    };

    const showCheckout=()=>(
        <div>
            {isAuthenticated() ? (
                   <button className="btn " style={{background:"orange"}}>
                       <b>Proceed to Payment -></b>
                    </button>
                ):(
                    <Link to="/signin">
                        <button className="btn btn-primary">
                            <b>Sign in Checkout -></b>
                        </button>
                    </Link>
            )}
        </div>
    );

    const buy=()=>{
        //we send nonce to server
        //nonce=data.instance.requestPaymentMethod();
        let nonce;
        let getNonce=data.instance.requestPaymentMethod()
            .then(data=>{
                //console.log(data);
                nonce=data.nonce;
                //we now send nonce to backend as paymentMethodNonce along with total amount
                //console.log("nonce sent:-"+nonce,getTotal(products));

                const paymentData={
                    paymentMethodNonce: nonce,
                    amount: getTotal(products)
                };

                processPayment(userId,token,paymentData)
                .then(response=>{   console.log(response);console.log(response.success);
                    setData({...data,success: response.success}); console.log(data.success);
                })
                .catch(err=>{
                    console.log(err);
                    setData({...data,error:err});
                });

            })
            .catch(err=>{
                //console.log('dropin error:',err.message);
                setData({...data,error: err.message});
            });
    }


    const showDropIn=()=>(
        <div onBlur={()=>setData({...data,error:""})}>
            {data.clientToken!=null && products.length>0 ? (
                <div >
                    <DropIn options={{
                            authorization: data.clientToken
                        }} onInstance={instance=>(data.instance=instance)}
                    />
                    <button onClick={buy}>Pay</button>
                </div>   
            ):(null)}
        </div>

    );

    const showError=error=>(
        <div className="alert alert-danger" style={{display:error?'':'none'}}>
            {error}
        </div>
    );

    const showSuccess=success=>(
        <div className="alert alert-info" style={{display:success?'':'none'}}>
            Payment Successful..!
        </div>
    );

    return (
        <Fragment>
            <table id="bill" className="mb-5">
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Price(Rs)</th>
                        <th>Quantity</th>
                        <th>Total(Rs)</th>
                    </tr>
                </thead>
                <tbody>
                {products.map((p,i)=>(
                    <tr key={i}>
                        <td>{p.name}</td>
                        <td>{p.price}</td>
                        <td>{p.count}</td>
                        <td>{p.count * p.price}</td>
                    </tr>
                ))}
                </tbody>
                <tfoot>
                    <tr >
                        <td colSpan="3"><center><b>Grand total:-</b></center></td>
                        <td style={{background:"#003399",color:"white"}}><b>Rs {getTotal()} </b></td>
                    </tr>
                </tfoot>
            </table>  

            <div style={{marginBottom :"100px",float: "right"}}>
                {showCheckout()}                
            </div>
            <div className="mt-5">
                {showError(data.error)}
                {showSuccess(data.success)}
                {showDropIn()}
            </div>

        </Fragment>

    )
};

export default Checkout;
