import React,{useState} from 'react';
import Layout from '../core/Layout';
import {API} from '../config';

const Signup=()=> {
    const [values,setValues]=useState({
        name:"",
        email:"",
        password:"",
        error:"",
        success:false
    });

    const {name,email,password}=values;

    //higher order function
    const handleChange=name=>event=>{
        setValues({...values,error:false, [name]:event.target.value});
    };

    const signup=(user)=>{          //user has name,email,password
        //console.log(name,email,password);
        fetch(`${API}/signup`,{             //fetch(url,object)
            method:"POST",      
            headers:{
                Accept:'application/json',          //tells the server that which content-type is understandable by the client 
                "Content-Type" : "application/json",    //this tells backend that we are sending a json object
            },
            body: JSON.stringify(user)
        })
        .then(response=>{
            return response.json();
        })
        .catch(err=>{
            console.log(err);
        });
    };

    const clickSubmit=(event)=>{
        event.preventDefault(); //this is done to prevent Default action on clicking button.
                                //The default action is that the page gets reloaded
        signup({name,email,password})
            //since the key and value are same in name:name,email:email,password:password ,so
            //we write this way
            .then(data=>{
                if(data.error){
                    setValues({...values,error: data.error, success:false});
                }else{
                    setValues({...values,
                            name:'',
                            email:'',
                            password:'',
                            error:'',
                            success:true
                    });
                }
            });    
    };

    const signUpForm=()=>(
        <form>
            <div className="form-group">
                <label className="text-muted">Name</label>
                <input onChange={handleChange('name')} type="text" className="form-control" value={name} />
            </div>
            <div className="form-group">
                <label className="text-muted">Email</label>
                <input onChange={handleChange('email')} type="email" className="form-control" value={email}/>
            </div>
            <div className="form-group">
                <label className="text-muted">Password</label>
                <input onChange={handleChange('password')} type="password" className="form-control" value={password}/>
            </div>
            <button onClick={clickSubmit} className="btn btn-primary">
                Submit
            </button>
        </form>
    );

    const showError=()=>{
        <div className="alert alert-danger" style={{display:error?'':'none'}}>
            {error}
        </div>
    }

    const showSuccess=()=>{
        <div className="alert alert-info" style={{display:success?'':'none'}}>
            Account successfully created. Please login to continue.
        </div>
    }

    return(
        <Layout title='Signup' description='Signup to E-commerce App' 
        className="container col-md-6 offset-md-2">
            {showError()}
            {showSuccess()}
            {signUpForm()}
            {JSON.stringify(values)}
        </Layout>
    );
};


export default Signup;
