import React, { useState } from 'react';
import {Link, Redirect} from 'react-router-dom';
import ShowImage from './ShowImage';
import {addItem,updateItem} from './cartHelpers';


const Card=({showAddToCart=true,cartUpdate=false,product,myStyle={backgroundImage: "linear-gradient(rgba(0,0,0,0.1),rgba(0,0,0,0.1))"}})=>{

    const [redirect,setRedirect]=useState(false);
    const [count,setCount]=useState(product.count);

    const addToCart=()=>{ //console.log("addtocart was invoked")
        addItem(product,()=>{
            setRedirect(true);  //console.log("reached callback")
        });
    };
 
    //var abc=showAddToCart?true:false;

    const showStock=quantity=>{
        return quantity>0 ? (
            quantity<5 ? (
                <span className="badge badge-warning badge-pill">Only {quantity} pieces left.Hurry..! Order Now.</span>
            ):(
                <span className="badge badge-primary badge-pill">In Stock</span>
            )
        ):(
            <span className="badge badge-danger badge-pill">Out of Stock</span>
        )
    };
    

    const handleChange=productId=>event=>{
        setCount(event.target.value<1 ? 1 : event.target.value);
        if(event.target.value>=1){
            updateItem(productId,event.target.value);
        }
    }


    const showCartUpdateOptions=cartUpdate=>{
        return cartUpdate && (
            <div className="input-group mb-3" >
                <div className="input-group-prepend">
                    <span className="input-group-text" style={{background:"#5c8a8a"}}>Adjust Quantity</span>
                </div>
                <input type="number" className="form-control"
                         value={count} onChange={handleChange(product._id)} />
            </div>
        )
    }
    
    const showAddToCartButton=(quantity)=>{
        return quantity>0 ?(
            <button onClick={addToCart} className="btn btn-outline-warning ml-2 ">
                Add to Cart
            </button>
        ):(
            <button className="btn btn-outline-warning ml-2" disabled>
                Add to Cart
            </button>
        )
    };
    
    const pageTop=()=>{
        return window.scrollTo(0, 0);
    }
    

    const shouldRedirect=redirect=>{
        if(redirect){
            return <Redirect to="/cart" />
        }
    }


    return(
        <div className="col-4 mb-3 " >
            
            <div className="card " style={{position:"relative"}} >
                <div className="card-header" style={{background:"#002b80",color:"white"}}>
                    <b>{product.name}</b>
                </div>
                <div className="card-body " style={myStyle}>
                    {shouldRedirect(redirect)}
                <center>
                    <div style={{width:"200px",height:"250px",position:"relative"}}>
                        <ShowImage item={product} url="product" /> 
                    </div>
                </center>    
                    <p style={{marginTop:"-50px"}}>{product.description.substring(0,25)}</p>
                    <p>Rs {product.price}</p>
                    <p>{showStock(product.quantity)}</p> 
                    <Link to={`/product/${product._id}`}>
                        <button className="btn btn-outline-primary mt-2 mb-2" 
                                    onClick={pageTop}>
                            View Product
                        </button>
                    </Link> 
                    {showAddToCart && showAddToCartButton(product.quantity)} 
                    {showCartUpdateOptions(cartUpdate)}
                </div>
            </div>
        </div>
    );
};

export default Card;
