import React,{useState,useEffect} from 'react';
import {Link} from 'react-router-dom';
import Layout from './Layout';
import {getCart} from './cartHelpers';
import Card from './Card';
import Checkout from './Checkout';


const Cart=()=>{
    const [items,setItems]=useState([]);

    useEffect(()=>{
        setItems(getCart());
    },[items]);     //whenever there is a change in "items", useEffect() will run again

/*
    const showItems=items=>{
        return (
            <div>
                <h2 >Your cart has {`${items.length}`} item(s)</h2>
                <hr/>
                {
                    items && items.map((product,i)=>(
                        <Card  key={i} product={product} />
                    ))
                }
            </div>
        )
    }

    const noItemsMessage=()=>(
        <h2>
            Your cart is empty.<br/>
            <Link to="/shop">Continue Shopping</Link>
        </h2>

    )
*/

    const showMessage=()=>(
        items.length? (
            <div>
                <h2 >Your cart has {`${items.length}`} item(s)</h2>
                <hr/>
            </div> 
        ):  (
        <div>
            <h2>
                Your cart is empty.<br/>
            </h2> 
            <h3>Goto <Link to="/shop">Shop</Link> and enjoy shopping.</h3>
            </div>
        )
    );
    
    return(
        <Layout title='Shopping Cart' 
            description='Manage all that you wish to buy . Happy Shopping :)' className="container-fluid">
            {showMessage()}
            <div className="row">
                {items && items.map((product,i)=>(
                        <Card  key={i} product={product} showAddToCart={false} cartUpdate={true} 
                            showRemoveProductButton={true}    />
                ))}            
            </div>
            <div className="mt-5">
                <h2>Cart Summary</h2>
                <hr/>
                <Checkout products={items} />
            </div>


        </Layout>
    )


};


export default Cart; 

