import React,{useState,useEffect} from "react";
import Layout from '../core/Layout';
import {isAuthenticated} from '../auth';
import { Link,Redirect} from 'react-router-dom';
import {read,update,updateUser} from './apiUser';

const Profile=({match})=>{    //helps in accessing contents of URL
    const [values,setValues]=useState({
        name:"",
        email:"",
        password:"",
        confirmPassword:"",
        error:false,
        success:false
    }); 

    const {name,email,password,success,error,confirmPassword}=values;
    const {token}=isAuthenticated();

    const init=(userId)=>{  console.log(match.params)
        read(userId,token)
            .then(data=>{
                if(data.error){
                    setValues({...values,error:true});
                }else{
                    setValues({...values,name:data.name,email:data.email});
                }
            });
    }; 

    useEffect(()=>{
        init(match.params.userId);
    },[]);


    const handleChange=name=>e=>{
        setValues({...values,error:false,[name]:e.target.value});   
    }

    const clickSubmit=e=>{
        e.preventDefault();
        if(confirmPassword===password){
        update(match.params.userId,token,{name,email,password})
            .then(data=>{
                if(data.error){
                    console.log(data.errror);
                }else{
                    //updating in localStorage
                    updateUser(data,()=>{
                        setValues({...values,name:data.name,email:data.email,success:true})
                    });
                }
            });
        }else{
            setValues({...values,confirmPassword:'',error:"Password and Confirm Password do not match"});
        }    
    };

    const redirectUser=(success)=>{
        if(success){
            return <Redirect to="/user/dashboard" />
        }
    }

    const showError=()=>(
        <div className="alert alert-danger" style={{display:error?'':'none'}}>
            {error}
        </div>
    );

    const profileUpdate=(name,email,password)=>(
        <form>
            <div className="form-group">
                <label className="text-muted">Name</label>
                <input type="text" onChange={handleChange('name')} className="form-control" value={name}/>
            </div>
            <div className="form-group">
                <label className="text-muted">Email</label>
                <input type="email" onChange={handleChange('email')} className="form-control" value={email}/>
            </div>
            <div className="form-group">
                <label className="text-muted">Password</label>
                <input type="password" onChange={handleChange('password')} className="form-control" value={password}/>
            </div>
            <div className="form-group">
                <label className="text-muted">Confirm Password</label>
                <input type="password" onChange={handleChange('confirmPassword')} className="form-control" value={confirmPassword}/>
            </div>
            <button onClick={clickSubmit} className="btn btn-primary">Submit</button>
        </form>
    )

    return(
        <Layout title='Profile' description='Update your profile' className="container-fluid">
            <div className="mr-5 ml-5 pr-5 pl-9">
                <h2 className="mb-4">Profile Update</h2>
                {showError()}
                {profileUpdate(name,email,password)}
                {redirectUser(success)}
            </div>
        </Layout>
    );


};

export default Profile;
